<?php if (!defined('BASEPATH')) exit('No direct script access allowed');/** * Start * Call all statics pages */class Reportes extends MY_Controller {        function __construct() {        parent::__construct();        // Force encrypted connection        $this->police->force_ssl();        $this->load->model('Registro_model');        //la variable evento se carga con todo la configuracion        $this->evento = $data['evento'] = $this->Registro_model->configura_evento();                        // Control police start permission and authentication        $data['documents'] = $this->police->police_control();        $this->load->vars($data);    }    //--------------------------------------------------------------------------    /**     * Listado de todos los cupones     *      */    function index() {        $data['repo'] = 'active';        $data['main_content'] = 'reportes/reportes';        $this->load->view(config_item('template') . 'main', $data);    }    //--------------------------------------------------------------------------      /**     * Listado de todos los cupones     *      */    function participacion() {        $data['links'] = array ('css/paginator.css');         $data['jscript'] = array ('js/vendor/jquery.ui.touch-punch.min.js',            'js/vendor/jquery.pagination.js',            'js/presparticipantes.js');        $data['contenido'] = $this->Registro_model->repo_participacion();         $this->load->view('reportes/participantes', $data);    }    //--------------------------------------------------------------------------      /**     * Listado de todos los cupones     *      */    function excel() {            $file="demo.xls";            $test="<table ><tr><td>Cell 1</td><td>Cell 2</td></tr></table>";            header("Content-type: application/vnd.ms-excel; name='excel'");            header("Content-Disposition: attachment; filename=$file");            $data['contenido'] = $this->Registro_model->repo_participacion();            $this->load->view('reportes/participantes', $data);    }    //--------------------------------------------------------------------------      }/* End of file administracion.php *//* Location: ./application/controllers/administracion.php */